import React,{useState} from "react";
import "./styles.css";

const UseState = () => {
  // const initialData = 15;
  const [myNum, setMyNum] = useState(10);
  const [disp,setdisp] = useState('invisible');
  const evaluate = (args)=>{
    if (args === '+'){
      setMyNum(myNum+1);
      if (disp === 'visible')
      setdisp('invisible')
    }
    else{
      if (myNum>0)
      setMyNum(myNum-1);
      else
      setdisp('visible');
    }
  }
  return (
    <>
      <div className="center_div">
        <div className="divi">

        <p>{myNum}</p>
        <span className={disp}>Value must be greater than zero, cannot decreament</span>
        </div>
        <div className="button2" onClick={()=>evaluate('+')} >
          <span></span>
          <span></span>
          <span></span>
          INCR
        </div>
        <div className="button2" onClick = {() => evaluate('-')}>
          <span></span>
          <span></span>
          <span></span>
          <span></span>
          DECR
        </div>
      </div>
    </>
  );
};

export default UseState;
