import React,{useState} from "react";
import "./styles.css";

const UseState = () => {
  // const initialData = 15;
  const [myNum, setMyNum] = useState(10);
  const evaluate = (args)=>{
    if (args === '+'){
      setMyNum(myNum+1);
    }
    else{
      if (myNum>0)
      setMyNum(myNum-1);

    }
  }
  return (
    <>
      <div className="center_div">
        <p>{myNum}</p>
        <div class="button2" onClick={()=>evaluate('+')} >
          <span></span>
          <span></span>
          <span></span>
          <span></span>
          INCR
        </div>
        <div class="button2" onClick = {() => evaluate('-')}>
          <span></span>
          <span></span>
          <span></span>
          <span></span>
          DECR
        </div>
      </div>
    </>
  );
};

export default UseState;
